#include<iostream>#include<algorithm>using namespace std;void add(int *b, int idx, int n, int size){	while(idx<= size){		b[idx] += n;		idx +=  idx&(-idx);	}	}int sum(int *b, int idx1, int idx2){	idx1--;	int idx = idx1;	int s1=0,s2=0;		while(idx){		s1 += b[idx];		idx -=  idx&(-idx);	}	idx = idx2;	while(idx){		s2 += b[idx];		idx -=  idx&(-idx);	}		cout<<"s2="<<s2<<" and s1="<<s1<<endl;	return s2-s1;}int main(){	int i,j,k,l;	int a[18+1]={0,12,24,4,6,9,34,1,8,-7,2,8,3,82,-9,92,12,54,9};	int bit[18+1];	for(i=1;i<=18;i++){		k =  i&(-i);		l=0;		cout<<"for i="<<i<<" k="<<k<<endl;		while(k--){			l=l+a[i-k];		}		bit[i]=l;	}	for(i=1;i<=18;i++) cout<<bit[i]<<' ';		add(bit,7,1,18);	for(i=1;i<=18;i++) cout<<bit[i]<<' ';	cout<<"...."<<sum(bit,5,8)<<endl;		return 0;}